import React, {useState} from "react";
import Note from "./Note"
import CreateNote from "./CreateNote";
import axios from "axios"

function App() {
    const [notes, setNotes] = useState([])

    function handleCreate(fullNote) {
        setNotes(prevValue => {
            return [
                ...prevValue,
                {
                    title: fullNote.title,
                    content: fullNote.content
                }
            ]
        })
    }

    const apiGet = async () => {
        await axios
            .get(`http://localhost:8080/export`)
            .then((response) => {
                setNotes(response.data)
            })
    }

    apiGet()

    return (
        <div>
            <h1>Hello world</h1>
            <CreateNote onAdd={handleCreate}/>
            {notes.map((item, index) => {return <Note key={index} title={item.title} content={item.content} />})}
            <button onClick={apiGet}>refresh from database</button>
        </div>
    )
}

export default App;
