import React, {useState} from "react";
import axios from "axios";
import styles from "./index.css"


function CreateNote() {
    const [fullNote, setFullNote] = useState({
        title: "",
        content: ""
    })

    function handleChange(event) {
        const {name, value} = event.target;
        setFullNote(prevValue => {
            return {
                ...prevValue,
                [name]: value
            }
        })
    }

    const apiPost = async () => {
        await axios
            .post(`http://localhost:8080/import`, {
                title: fullNote.title,
                content: fullNote.content
            })
            .then((response) => {
                console.log(response)
            })
    }

    return (
        <div>
            <div>
                <label for="large-input" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Large input</label>
                <input type="text" id="large-input" class="block w-full p-4 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 text-base focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"></input>
            </div>
            {/* <input onChange={handleChange} value={fullNote.title} name="title" type="text" />
            <textarea onChange={handleChange} value={fullNote.content} name="content" id="" cols="30" rows="10" />
            <button onClick={apiPost}>post note</button> */}
        </div>
    )
}

export default CreateNote;
