import React, {useState} from "react";
import Note from "./Note"
import CreateNote from "./CreateNote";

function App() {
    const [notes, setNotes] = useState({})

    function handleCreate(fullNote) {
        setNotes(prevValue => {
            return [
                prevValue,
                {
                    title: fullNote.title,
                    content: fullNote.content
                }
            ]
        })
    }

    return (
        <div>
            <h1>Hello world</h1>
            <CreateNote onAdd={handleCreate}/>
            {notes.map((item) => {return <Note title={item.title} content={item.content} />})}
        </div>
    )
}

export default App;
