import React, {useState} from "react";
import axios from "axios";


function CreateNote(props) {
    const [fullNote, setFullNote] = useState({
        title: "",
        content: ""
    })

    function handleChange(event) {
        const {name, value} = event.target;
        setFullNote(prevValue => {
            return {
                ...prevValue,
                [name]: value
            }
        })
    }

    return (
        <div>
            <input onChange={handleChange} value={fullNote.title} name="title" type="text" />
            <textarea onChange={handleChange} value={fullNote.content} name="content" id="" cols="30" rows="10" />
            <button onClick={() => {props.onAdd(fullNote)}}>Create note</button>
        </div>
    )
}

export default CreateNote;
