import React, {useState} from "react";
import Note from "./Note"
import CreateNote from "./CreateNote";
import axios from "axios"
import Header from "./Header"
import Footer from "./Footer"

function App() {
    const [notes, setNotes] = useState([])

    const apiGet = async () => {
        await axios
            .get(`http://localhost:8080/export`)
            .then((response) => {
                setNotes(response.data)
            })
    }

    const apiPost = async (id) => {
        await axios
            .post(`http://localhost:8080/delete`, {
                id: id+1
            })
            .then((response) => {
                console.log(response.data)
            })
    }

    apiGet()

    return (
        <div>
            <Header/>
            <CreateNote />
            <div className="notes-parent">
                {notes.map((item, index) => {return <Note id={index} key={index} title={item.title} content={item.content} onDelete={apiPost} />})}
            </div>
            <Footer />
            
        </div>
    )
}

export default App;
